import React, { useState } from 'react';

const Themes = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const themeSlides = [
    {
      title: 'AI/ML',
      description: 'Build intelligent applications using cutting-edge AI tools and machine learning algorithms',
      icon: '🤖',
      color: 'from-blue-500 to-cyan-500',
      bgColor: 'bg-gradient-to-br from-blue-400 to-cyan-300'
    },
    {
      title: 'IoT',
      description: 'Innovate with connected devices and create smart solutions for the Internet of Things',
      icon: '🌐',
      color: 'from-green-500 to-emerald-500',
      bgColor: 'bg-gradient-to-br from-green-400 to-emerald-300'
    },
    {
      title: 'Web Development',
      description: 'Create full-stack applications with modern frameworks and technologies',
      icon: '💻',
      color: 'from-purple-500 to-pink-500',
      bgColor: 'bg-gradient-to-br from-purple-400 to-pink-300'
    },
    {
      title: 'Cyber Security',
      description: 'Solve real-world security challenges and protect digital assets',
      icon: '🔒',
      color: 'from-red-500 to-orange-500',
      bgColor: 'bg-gradient-to-br from-red-400 to-orange-300'
    },
    {
      title: 'Embedded Systems',
      description: 'Work on hardware-software integration projects and embedded solutions',
      icon: '⚡',
      color: 'from-yellow-500 to-amber-500',
      bgColor: 'bg-gradient-to-br from-yellow-400 to-amber-300'
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % themeSlides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + themeSlides.length) % themeSlides.length);
  };

  return (
    <section id="themes" className="section-padding">
      <div className="container-custom">
        <div className="text-center mb-16">
          <h2 className="text-5xl md:text-6xl font-bold mb-6">
            <span className="text-gradient">THEMES</span>
          </h2>
          <p className="text-xl text-gray-400 max-w-3xl mx-auto mb-8">
            Explore diverse technology domains and showcase your expertise in any area that excites you
          </p>
          
          {/* Main Theme */}
          <div className="max-w-2xl mx-auto mb-12">
            <div className="card text-center bg-gradient-to-r from-primary-600/10 to-primary-500/10 border-primary-500/30">
              <div className="text-6xl mb-4">🚀</div>
              <h3 className="text-3xl font-bold text-primary-400 mb-4">Open Innovation</h3>
              <p className="text-lg text-gray-300">
                The main theme for Aviothic 2.0 is <strong>Open Innovation</strong>. 
                We encourage participants to think outside the box and create solutions 
                that can make a real impact in the world.
              </p>
            </div>
          </div>
        </div>

        {/* Image Slider with Overlapping Cards */}
        <div className="relative max-w-6xl mx-auto mb-16">
          <div className="relative h-96 flex items-center justify-center">
            {/* Background with orange gradient */}
            <div className="absolute inset-0 bg-gradient-to-r from-orange-400 to-orange-500 rounded-3xl"></div>
            
            {/* Slider Container */}
            <div className="relative flex items-center justify-center space-x-4 w-full h-full">
              {/* Previous Button */}
              <button
                onClick={prevSlide}
                className="absolute left-4 z-20 bg-white/20 hover:bg-white/30 text-white p-3 rounded-full transition-all duration-200 hover:scale-110"
                aria-label="Previous theme"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>

              {/* Next Button */}
              <button
                onClick={nextSlide}
                className="absolute right-4 z-20 bg-white/20 hover:bg-white/30 text-white p-3 rounded-full transition-all duration-200 hover:scale-110"
                aria-label="Next theme"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </button>

              {/* Theme Cards with Overlapping Effect */}
              {themeSlides.map((theme, index) => {
                const isActive = index === currentSlide;
                const isPrev = index === (currentSlide - 1 + themeSlides.length) % themeSlides.length;
                const isNext = index === (currentSlide + 1) % themeSlides.length;
                
                let position = 'hidden';
                if (isActive) position = 'z-30 transform scale-100';
                else if (isPrev) position = 'z-20 transform scale-90 -translate-x-16 opacity-70';
                else if (isNext) position = 'z-20 transform scale-90 translate-x-16 opacity-70';

                return (
                  <div
                    key={theme.title}
                    className={`absolute transition-all duration-500 ease-in-out ${position}`}
                  >
                    <div className={`w-80 h-64 rounded-2xl border-4 border-white shadow-2xl ${theme.bgColor} flex flex-col items-center justify-center text-center p-6`}>
                      {/* Icon */}
                      <div className="text-8xl mb-4">
                        {theme.icon}
                      </div>
                      
                      {/* Title */}
                      <h3 className="text-3xl font-bold text-white mb-3">
                        {theme.title}
                      </h3>
                      
                      {/* Description */}
                      <p className="text-white/90 text-sm leading-relaxed">
                        {theme.description}
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Dots Indicator */}
          <div className="flex justify-center space-x-3 mt-8">
            {themeSlides.map((_, index) => (
              <button
                key={index}
                onClick={() => setCurrentSlide(index)}
                className={`w-3 h-3 rounded-full transition-all duration-300 ${
                  index === currentSlide 
                    ? 'bg-orange-400 scale-125' 
                    : 'bg-orange-200 hover:bg-orange-300'
                }`}
                aria-label={`Go to slide ${index + 1}`}
              />
            ))}
          </div>
        </div>

        {/* Call to Action */}
        <div className="text-center mt-16">
          <div className="card max-w-2xl mx-auto bg-gradient-to-r from-dark-800/50 to-dark-700/50">
            <h3 className="text-2xl font-bold text-white mb-4">
              Ready to Choose Your Domain?
            </h3>
            <p className="text-gray-400 mb-6">
              Select any domain that interests you or combine multiple technologies to create something unique. 
              The choice is yours!
            </p>
            <a
              href="https://forms.google.com/register"
              target="_blank"
              rel="noopener noreferrer"
              className="btn-primary"
            >
              Register Your Team
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Themes;
