import React from 'react';
import background from "../assests/background.png"; 

const Hero = () => {
  return (
    <section id="home" className="relative h-screen flex items-center justify-center overflow-hidden">
      {/* Background Image */}
      <div className="absolute inset-0 z-0">
        {/* Hackathon Background Image */}
        <div 
          className="w-full h-full bg-cover bg-center bg-no-repeat"
          style={{
            backgroundImage: `url(${background})`
          }}
        >
          {/* Dark Overlay for better text readability */}
          <div className="absolute inset-0 bg-gradient-to-br from-dark-900/60 via-dark-800/50 to-dark-900/60"></div>
          
          {/* Tech Pattern Overlay */}
          <div className="absolute inset-0 opacity-30">
            <div className="absolute top-20 left-10 w-32 h-32 border border-neon-blue/40 rounded-full neon-glow"></div>
            <div className="absolute top-40 right-20 w-24 h-24 border border-neon-green/40 rounded-full neon-glow-green"></div>
            <div className="absolute bottom-40 left-20 w-40 h-40 border border-neon-pink/40 rounded-full neon-glow-pink"></div>
            <div className="absolute bottom-20 right-10 w-28 h-28 border border-neon-blue/40 rounded-full neon-glow"></div>
            
            {/* Grid Pattern */}
            <div className="absolute inset-0" style={{
              backgroundImage: `radial-gradient(circle at 1px 1px, rgba(0, 194, 255, 0.2) 1px, transparent 0)`,
              backgroundSize: '20px 20px'
            }}></div>
            
            {/* Code-like floating elements */}
            <div className="absolute top-1/4 left-1/4 text-neon-blue/30 text-6xl font-mono animate-pulse neon-glow">&lt;/&gt;</div>
            <div className="absolute top-1/3 right-1/4 text-neon-green/30 text-4xl font-mono animate-pulse neon-glow-green">{};</div>
            <div className="absolute bottom-1/3 left-1/3 text-neon-pink/30 text-5xl font-mono animate-pulse neon-glow-pink">()</div>
            <div className="absolute bottom-1/4 right-1/3 text-neon-blue/30 text-3xl font-mono animate-pulse neon-glow">[]</div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="relative z-10 container-custom text-center flex flex-col justify-center h-full">
        <div className="max-w-5xl mx-auto">
          {/* Main Title */}
          <h1 className="text-7xl md:text-8xl lg:text-9xl font-bold mb-8 animate-fade-in">
            <span className="bg-gradient-to-r from-neon-blue to-neon-green drop-shadow-2xl bg-clip-text text-transparent" style={{ textShadow: '0 0 20px rgba(0, 194, 255, 0.8)' }}>Aviothic 2.0</span>
          </h1>
          
          {/* Subtitle */}
          <p className="text-3xl md:text-4xl lg:text-5xl text-dark-100 mb-10 animate-fade-in font-light" style={{ animationDelay: '0.2s' }}>
            Code • Create • Collaborate
          </p>
          
          {/* Description */}
          <p className="text-2xl md:text-3xl text-dark-200 mb-16 max-w-4xl mx-auto animate-fade-in leading-relaxed" style={{ animationDelay: '0.4s' }}>
            Join the ultimate internal hackathon experience. Build innovative solutions, 
            collaborate with peers, and showcase your technical prowess in a 24-hour coding marathon.
          </p>
          
          {/* CTA Button */}
          <div className="animate-fade-in" style={{ animationDelay: '0.6s' }}>
            <a
              href="https://forms.google.com/register"
              target="_blank"
              rel="noopener noreferrer"
              className="btn-primary text-2xl px-16 py-8 inline-block shadow-2xl hover:shadow-primary-500/25 transform hover:scale-105 transition-all duration-300"
            >
              Register Now
            </a>
          </div>
          
        </div>
      </div>

      {/* Scroll Indicator */}
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce">
        <div className="w-6 h-10 border-2 border-neon-blue rounded-full flex justify-center neon-glow">
          <div className="w-1 h-3 bg-neon-blue rounded-full mt-2 animate-pulse"></div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
