import React, { useState } from 'react';

const Rewards = () => {
  const [currentReward, setCurrentReward] = useState(0);

  const rewards = [
    {
      title: 'Prize Pool',
      amount: '₹8,000',
      description: 'Total prize money to be distributed among winners',
      icon: '🏆',
      color: 'from-yellow-500 to-amber-500'
    },
    {
      title: 'Medals',
      description: 'Exclusive medals for top 3 teams',
      icon: '🥇',
      color: 'from-yellow-400 to-yellow-600'
    },
    {
      title: 'Certificates',
      description: 'Participation certificates for all participants',
      icon: '📜',
      color: 'from-blue-500 to-blue-600'
    },
    {
      title: 'Goodies',
      description: 'Exciting goodies for all participants',
      icon: '🎁',
      color: 'from-purple-500 to-pink-500'
    }
  ];

  const nextReward = () => {
    setCurrentReward((prev) => (prev + 1) % rewards.length);
  };

  const prevReward = () => {
    setCurrentReward((prev) => (prev - 1 + rewards.length) % rewards.length);
  };

  const handleBoxClick = () => {
    nextReward();
  };

  return (
    <section id="rewards" className="section-padding bg-dark-800/30">
      <div className="container-custom">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-bold mb-6">
            <span className="text-gradient">Rewards</span> & Recognition
          </h2>
        </div>


        {/* Mobile/Tablet Slider */}
        <div className="lg:hidden">
          <div className="relative max-w-sm mx-auto">
            <div 
              className="card p-0 overflow-hidden h-80"
              onTouchStart={handleTouchStart}
              onTouchEnd={handleTouchEnd}
            >
              <div className="relative h-full">

                {/* Reward Card */}
                <div className="p-6 text-center h-full flex flex-col justify-center">
                  {/* Icon */}
                  <div className={`w-16 h-16 mx-auto mb-4 rounded-2xl bg-gradient-to-r ${rewards[currentReward].color} flex items-center justify-center text-3xl`}>
                    {rewards[currentReward].icon}
                  </div>

                  {/* Title */}
                  <h3 className="text-xl font-bold text-white mb-2">
                    {rewards[currentReward].title}
                  </h3>

                  {/* Amount (if applicable) */}
                  {rewards[currentReward].amount && (
                    <div className="text-2xl font-bold text-primary-400 mb-3">
                      {rewards[currentReward].amount}
                    </div>
                  )}

                  {/* Description */}
                  <p className="text-gray-400 text-sm leading-relaxed">
                    {rewards[currentReward].description}
                  </p>
                </div>
              </div>
            </div>

            {/* Dots Indicator */}
            <div className="flex justify-center space-x-2 mt-6">
              {rewards.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentReward(index)}
                  className={`w-3 h-3 rounded-full transition-all duration-200 ${
                    index === currentReward 
                      ? 'bg-primary-500 scale-125' 
                      : 'bg-gray-600 hover:bg-gray-500'
                  }`}
                  aria-label={`Go to reward ${index + 1}`}
                />
              ))}
            </div>
          </div>
        </div>

        {/* Desktop Grid */}
        <div className="hidden lg:grid grid-cols-4 gap-8">
          {rewards.map((reward, index) => (
            <div
              key={reward.title}
              className="card group hover:scale-105 transition-all duration-300 text-center"
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              {/* Icon */}
              <div className={`w-16 h-16 mx-auto mb-6 rounded-2xl bg-gradient-to-r ${reward.color} flex items-center justify-center text-3xl group-hover:scale-110 transition-transform duration-300`}>
                {reward.icon}
              </div>

              {/* Title */}
              <h3 className="text-xl font-bold text-white mb-2 group-hover:text-primary-400 transition-colors duration-300">
                {reward.title}
              </h3>

              {/* Amount (if applicable) */}
              {reward.amount && (
                <div className="text-2xl font-bold text-primary-400 mb-4">
                  {reward.amount}
                </div>
              )}

              {/* Description */}
              <p className="text-gray-400 mb-4 group-hover:text-gray-300 transition-colors duration-300">
                {reward.description}
              </p>
            </div>
          ))}
        </div>

      </div>
    </section>
  );
};

export default Rewards;
